import React from 'react';
import styled from "styled-components"

const DivContainer = styled.div `
    height:85vh;
    width: 100vw;
    display:flex;
     justify-content: start;
    flex-direction: column;
`

const Filtro = styled.div`
    height: 8vh;
    width: 100vw;
    display:flex;
    flex-direction: row;
    justify-content: space-around;
    align-items:center;
`
const DivInputs = styled.div `
    width: 80%;
    flex-direction: row;
    justify-content: space-evenly;
`

const InputBusca = styled.input`
margin-right: 5px;
height: 20px;
`

const InputValor = styled.input `
height: 20px;
`

const BotaoCarrinho = styled.button `
height: 50px;
width: 80px;
`
const DivProdutos = styled.div`
 display: flex;
`

const DivProduto = styled.div`
    margin: 20px;
    width: 200px;
    
  img{
        width: 100%;
    }
`

export default class Home extends React.Component{
    state = {
        produtos: [
            {
                id:1,
                name: "A Camisa bonita",
                value: 32.40,
                imgUrl: "data:image/jpeg;base64,",
                quantidade: 0
            },
            {
                id:2,
                name:  "A Camisa bonita",
                value: 10,
                imgUrl: "data:image/jpeg;base64,",
                quantidade: 0
            },
            {
                id:3,
                name:  "A Camisa bonita",
                value: 200,
                imgUrl: "data:image/jpeg;base64,",
                quantidade: 0
            },
            {
                id:4,
                name:  "A Camisa bonita",
                value: 100,
                imgUrl: "data:image/jpeg;base64,",
                quantidade: 0
            }
        ],

        valorMinimo: 0,
        valorMaximo: 0,
    }

    componentDidUpdate() {
        localStorage.setItem("Produtos", this.state.produtos)
    }
    

    handleMinimo = (event) => {
        this.setState({valorMinimo: event.target.value})
    }

    handleMaximo = (event) => {
        this.setState({valorMaximo: event.target.value})
    }

    handleNome = (event) => {
        this.setState({valorNome: event.target.value})
    }


    commparaProduto = (id) => {
        const produtos = this.state.produtos;

        produtos.forEach((item, index) => {
             if(item.id === id){
             let produtosAtualizado = [...this.state.produtos];
                produtosAtualizado[index].quantidade =  produtosAtualizado[index].quantidade + 1;
                this.setState({produtos: produtosAtualizado})
             }
        })
        console.log(this.state.produtos);
    } 

    render(){
    
        return (<DivContainer >

            <Filtro>
            <DivInputs>
            <InputValor onChange={this.handleMinimo} placeholder="Valor mínimo" type="number"/>
            <InputValor onChange={this.handleMaximo} placeholder="Valor máximo" type="number"/>
            <InputBusca onChange={this.handleNome} placeholder="Buscar produto..." />
            </DivInputs>
            <BotaoCarrinho onClick={this.props.mudarPagina}> Carrinho </BotaoCarrinho>
            </Filtro>

            <DivProdutos>
            {(this.state.produtos).map((item, index) => {
                return <DivProduto>
                    <img src={item.imgUrl}/>
                    <h3>{item.name}</h3>
                    <h3>R$ {item.value}</h3>
                    <button onClick={() => this.commparaProduto(item.id)}>Comprar</button>
                </DivProduto>
            })}
            </DivProdutos>
        </DivContainer>)
    }
}